mapper.insert:
INSERT INTO students
(
	students_name,
	students_password
)
VALUES
(
	#{studentsName},
	#{studentsPassword}
)

mapper.update:
UPDATE students
SET students_name = #{studentsName},
	students_password = #{studentsPassword}
WHERE students_id = #{studentsId}

